import React, { Component } from 'react';
import local from '../../data/local';
import entertainment from '../../data/entertainment';
import health from '../../data/health';
import science from '../../data/science';
import tech from '../../data/technology'

import './App.css';
import Menu from '../Menu/Menu';
import SearchForm from '../SearchForm/SearchForm';
import NewsContainer from '../NewsContainer/NewsContainer';

class App extends Component {
  constructor() {
    super();
    this.state = {
      local: local,
      entertainment: entertainment,
      health: health,
      science: science,
      tech: tech,
      // allNews: [local, entertainment, health, science, tech],
      displayedNews: health
    }
  }

  sendNews = (newsType) => {
    let stateKeys = Object.keys(this.state)

    let newsToDisplay = stateKeys.filter(news => {

      console.log(news)
     return news === this.state[newsType]
   })

    this.setState({displayedNews:newsToDisplay})
  }

  render () {
    return (
      <div className="app">
        <Menu sendNews = {this.sendNews}/>
        <SearchForm />
        <NewsContainer news = {this.state.displayedNews}/>
      </div>
    );
  }
}

export default App;
