import React from 'react';
import './Menu.css'
import LocalZNews from '../../icons/location-icon.svg';
import Technology from '../../icons/gear.svg';
import Entertainment from '../../icons/movie-camera.svg';
import Science from '../../icons/planet.svg';
import Health from '../../icons/360.svg'




const Menu = () => {

  const navButtons = [LocalZNews, Technology, Entertainment, Science, Health]

  const newsHeading = navButtons.map(heading => {
    return (
      <p className="section-heading">
        <img className="section-img"
          src={heading}
          alt="location pin">
        </img>
      {heading}
      </p>
    )
  })

  return(
    <nav className= "menu">
      <p className="heading-one">Send
        <span className="heading-two">News</span>
      </p>


    </nav>

  )
}

export default Menu;


